    private string BuildCss()
    {
        var css = Size switch
        {
            PureSize.ExtraSmall => "w-3 h-3",
            PureSize.Small => "w-4 h-4",
            PureSize.Medium => "w-5 h-5",
            PureSize.Large => "w-6 h-6",
            PureSize.ExtraLarge => "w-7 h-7",
            _ => "w-6 h-6"
        };

        if (Animate == PureAnimate.Spin)
        {
            css = $"{css} animate-spin";
        }

        return css;
    }
